import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import frutales from '../assets/bannerFrutal.png'
import cerco from '../assets/bannerCerco.png'
import { useFadeInOnScroll } from './useFadeInOnScroll';
import {useInView } from 'react-intersection-observer'
function Carrousel() {
  const fadeInClass = useFadeInOnScroll();
  const [ref, inView] = useInView({
    triggerOnce: true, // Esto es para que el fadeIn se ejecute una sola vez
});
  return (
    <div ref={ref} className={`my-component ${fadeInClass}`}>
    <div className='carrouselContainer'>
      <div className='carrouselInterno'> 
      <Carousel interval={8000} slide>
      <Carousel.Item>
        <img className="d-block w-100" src={frutales} alt="Banner frutal"/>
      </Carousel.Item>
      <Carousel.Item>
        <img className="d-block w-100" src={cerco} alt="Banner cerco"/>
      </Carousel.Item>
    </Carousel>
      </div>
    </div>
    </div>
      );
    }

export default Carrousel;
