import React, { useEffect, useState } from 'react';
import withFadeIn from './withFadeIn';

const FadeInScrollComponent = ({ children }) => {
  const [isVisible, setVisible] = useState(false);

  const onVisibilityChange = () => {
    if (window.scrollY > 50) {
      setVisible(true);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', onVisibilityChange);
    return () => {
      window.removeEventListener('scroll', onVisibilityChange);
    };
  }, []);

  const FadeInChild = withFadeIn(() => children);
  return <FadeInChild className={`my-component ${isVisible ? 'fade-in' : ''}`} />;
};

export default FadeInScrollComponent;
