import React, { useState, useEffect } from 'react';
import { Fade } from 'react-bootstrap';
import { SeccionImagen } from './SeccionImagen';

const FadeInComponent = () => {
    const [showComponent, setShowComponent] = useState(false);
  
    const handleScroll = () => {
      const componentPosition = document.getElementById('fade-in-component').getBoundingClientRect().top;
      const windowHeight = window.innerHeight;
  
      if (componentPosition < windowHeight) {
        setShowComponent(true);
      }
    };
  
    
    useEffect(() => {
      window.addEventListener('scroll', handleScroll);
      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);
  
    return (
      <div id="fade-in-component">
        <Fade in={showComponent}>
          <div className="fade-in-content">
            <SeccionImagen/>
          </div>
        </Fade>
      </div>
    );
  };
  
  export default FadeInComponent;
